import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Form from './Form';
import Grid from './Grid';

const MainComponent = () => {
  const [users, setUsers] = useState([]);
  const [onEdit, setOnEdit] = useState(null);

  useEffect(() => {
    // Lógica para buscar os usuários inicialmente
    getUsers();
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get('http://localhost:8800/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Erro ao obter usuários:', error);
    }
  };

  const handleEdit = (user) => {
    setOnEdit(user);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:8800/users/${id}`);
      setUsers(users.filter((user) => user.id !== id));
      toast.success('Usuário excluído com sucesso!');
    } catch (error) {
      console.error('Erro ao excluir usuário:', error);
      toast.error('Erro ao excluir usuário.');
    }
  };

  const handleFormSubmit = async (userData) => {
    try {
      if (onEdit) {
        // Lógica para editar usuário
      } else {
        // Lógica para criar usuário
      }
      // Atualiza a lista de usuários após a criação/edição
      getUsers();
    } catch (error) {
      console.error('Erro ao salvar usuário:', error);
      toast.error('Erro ao salvar usuário.');
    }
  };

  return (
    <div>
      <h1>Gerenciamento de Usuários</h1>
      <Form onSubmit={handleFormSubmit} onEdit={onEdit} setOnEdit={setOnEdit} />
      <Grid users={users} setUsers={setUsers} handleEdit={handleEdit} handleDelete={handleDelete} />
    </div>
  );
};




export default Form;
